ls | grep -v "index.html" | grep -v "createIndex.sh" | perl -e 'print "<head><title>Downloads</title>        </head><html><body style=\"font-family:Helvetica, Arial; background-color:#fdfdfd;padding: 25px;color:#333;\"><h1>Downloads</h1><ul>"; while(<>) { chop $_; print "<li><a href=\"./$_\">$_</a></li>";} print "</ul></body></html>"' > index.html
